# higher resultion time signal (at least on Mac OS X)
class ArtisanTime():
    def __init__(self):
        if sys.version < '3':
            self.clock = libtime.time()
        else:
            self.clock = libtime.perf_counter()

    def setHMS(self,*_):
        self.start()
        
    def start(self):
        if sys.version < '3':
            self.clock = libtime.time()
        else:
            self.clock = libtime.perf_counter()
        
    def elapsed(self):
        if sys.version < '3':
            return (libtime.time() - self.clock)*1000.
        else:
            return (libtime.perf_counter() - self.clock)*1000.

    


