            FCs_temp = 0
            FCs_temp_count = 0            
            DROP_time = 0
            DROP_time_count = 0
            DROP_temp = 0
            DROP_temp_count = 0
            DRY_percent = 0
            DRY_percent_count = 0
            MAI_percent = 0
            MAI_percent_count = 0
            DEV_percent = 0
            DEV_percent_count = 0
            AUC = 0
