                                if cs.startswith('out(') and len(cs)>7:
                                    cs_split = cs[4:-1].split(',')
                                    if len(cs_split)>1:
                                        channel=toInt(cs_split[0])
                                        int_value = toInt(eval(cs_split[1])) # pylint: disable=eval-used
                                        if len(cs_split) == 2:
                                            self.ser.phidgetOUTsetPWM(channel, int_value)
