                f.close()
                profile = self.deserialize(filename)
                tb = profile["timex"]
                t1 = profile["temp1"]
                t2 = profile["temp2"]
                self.qmc.temp1B,self.qmc.temp2B,self.qmc.timeB = t1,t2,tb
