                b2 = self.qmc.smooth_list(tb,t2,window_len=self.qmc.curvefilter)
                # NOTE: parallel assignment after time intensive smoothing is necessary to avoid redraw failure!
                self.qmc.stemp1B,self.qmc.stemp2B = b1,b2
